import { ref } from "vue";
export default {
  __name: 'App',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const price = ref(0);
    const quantity = ref(0);
    const amount = ref(0);
    const counter = ref(0);
    const events = ref([]);
    // const previewData = ref({});
    const isPreview = ref(false);
    const test = ref(localStorage.getItem('data') || '{}');
    // const localStorageData = computed(() => {
    //   return JSON.parse(localStorage.getItem('data') || '{}')
    // });

    const calculateFields = changedField => {
      setTimeout(() => {
        if (changedField === 'price' || changedField === 'quantity') {
          amount.value = price.value * quantity.value;
        } else if (changedField === 'amount') {
          if (price.value) {
            quantity.value = amount.value / price.value;
          } else if (quantity.value) {
            price.value = amount.value / quantity.value;
          }
        }
      }, 300);
      saveEvent(`Изменено поле: ${changedField}`, {
        price: price.value,
        quantity: quantity.value,
        amount: amount.value
      });
    };
    const saveEvent = (message, data = null) => {
      const event = {
        message,
        data
      };
      events.value.unshift(event);
    };
    const postAmount = () => {
      const data = {
        counter: counter.value++,
        price: price.value,
        quantity: quantity.value,
        amount: amount.value
      };
      isPreview.value = true;
      serverRequest(data).then(() => {
        // console.log(res)
        localStorage.setItem('data', JSON.stringify(data));
        isPreview.value = false;
      }).catch(err => {
        throw new Error(err.message);
      });
    };
    const serverRequest = data => {
      return new Promise((resolve, reject) => {
        setTimeout(() => {
          const isSuccessRequest = data.amount % 2 === 0;
          if (isSuccessRequest) {
            resolve({
              success: true
            });
          } else {
            reject({
              success: false
            });
          }
        }, 1000);
      });
    };
    const __returned__ = {
      price,
      quantity,
      amount,
      counter,
      events,
      isPreview,
      test,
      calculateFields,
      saveEvent,
      postAmount,
      serverRequest,
      ref
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
