import { ref } from "vue";
import HelloWorld from "@/components/HelloWorld.vue";
export default {
  __name: 'App',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const price = ref(0);
    const quantity = ref(0);
    let amount = ref(0);
    const events = ref([]);
    let debounceTimer = null;
    const calculateFields = changedField => {
      if (changedField === 'price') {
        amount.value = price.value * quantity.value;
      } else if (changedField === 'quantity') {
        amount.value = price.value * quantity.value;
      }
    };
    const debounceInput = field => {
      clearTimeout(debounceTimer);
      debounceTimer = setTimeout(() => {
        calculateFields(field);
        logEvent(`Изменено поле: ${field}`, {
          price: price.value,
          quantity: quantity.value,
          amount: amount.value
        });
      }, 300);
    };
    const logEvent = (message, data = null) => {
      const event = {
        timestamp: new Date().toISOString(),
        message,
        data
      };
      events.value.unshift(event); // Add to beginning to show newest first
    };
